REPLACE INTO Language_DE_DE (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_BRANCH_UNLOCK_SPEND', '[COLOR_POSITIVE_TEXT] Diese Richtlinie Baum freigeschaltet werden können! [ENDCOLOR] [NEWLINE] Klicken Sie hier, um zu verbringen {1_Num} [ICON_CULTURE] Kultur und öffnen Sie diese Sozialpolitik Baum.', '', '');
