REPLACE INTO Language_ES_ES (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_BRANCH_UNLOCK_SPEND', '[COLOR_POSITIVE_TEXT] Este árbol de directivas se puede desbloquear! [ENDCOLOR] [NEWLINE] Haga clic aquí para pasar {1_Num} [ICON_CULTURE] Cultura y abrir este árbol Política Social.', '', '');
