REPLACE INTO Language_FR_FR (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_BRANCH_UNLOCK_SPEND', '[COLOR_POSITIVE_TEXT] Cette arborescence de stratégie peut être déverrouillé! [ENDCOLOR] [NEWLINE] Cliquez ici pour passer {1_Num} [ICON_CULTURE] Culture et ouvrir cet arbre de la politique sociale.', '', '');
