REPLACE INTO Language_IT_IT (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_BRANCH_UNLOCK_SPEND', '[COLOR_POSITIVE_TEXT] Questo albero Policy può essere sbloccato! [ENDCOLOR] [NEWLINE] Clicca qui per trascorrere {1_Num} [ICON_CULTURE], Cultura e aprire l''albero delle Politiche Sociali.', '', '');
