REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_NOTIFICATION_MINOR_NOW_FRIENDS_BASE', '[ICON_INFLUENCE] wpływ na {1_CivName: textkey} wzrosła do punktu, w którym jesteś teraz [COLOR_POSITIVE_TEXT] PRZYJACIELE [ENDCOLOR] [NEWLINE] [NEWLINE] {2_DetailsString} Oni również dał Ci pozwolenie na przeniesienie Jednostek przez. ich terytorium.', '', '');
