REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_NOTIFICATION_MINOR_NOW_ALLIES_MARITIME', 'Jako [COLOR_POSITIVE_TEXT] morskiej [ENDCOLOR] miasto-państwo, teraz zrobić duże [ICON_FOOD] eksportu żywności do swoich miast.', '', '');
