REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_NOTIFICATION_MINOR_ALLIES_LOST', '[ICON_INFLUENCE] wpływ na {1_CivName: textkey} zmniejszyła się do punktu, w którym nie są już ich [COLOR_POSITIVE_TEXT] ALLY [ENDCOLOR] [NEWLINE] [NEWLINE] {2_DetailsString} [NEWLINE] [NEWLINE] jako takie. , już nie da ci swoje strategiczne i luksusowe Zasoby.', '', '');
