REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_SMITH_STRATEGY', 'Smith zwiększa produkcję w mieście.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_SMITH_PEDIA', 'Smith jest miejsce, w którym kowal pracuje metalu przez ogrzewanie i młotkiem, zazwyczaj żeliwa lub stali. Hetyci opracowany wytapiania rud żelaza około 1500 roku pne. Wydaje się, że utrzymuje się niemal monopol na wiedzę o produkcji żelaza na kilkaset lat, ale kiedy ich imperium upadł podczas przewrotów wschodnich śródziemnomorskich około 1200 roku pne, rozprzestrzeniania wiedzy we wszystkich kierunkach.', '', '');
