REPLACE INTO Language_RU_RU (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_MISC_YOU_CITY_WAS_CAPTURED', 'Защищая, ваш город {@ 1_CityName} был [COLOR_NEGATIVE_TEXT] захватил [ENDCOLOR] по {TXT_KEY_GRAMMAR_A_AN << {@ 3_owner_adjective}} {@ 2_EnUName} ([COLOR_POSITIVE_TEXT] {4_Num} [ ENDCOLOR] повреждение)!', '', '');
