REPLACE INTO Language_DE_DE (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_UNIT_HELP_HOPLITE', 'Soldat einzigartig für die Griechen. Hopliten beginnen mit der Phalanx Förderung, im Gegensatz zu der Speerträger, die sie ersetzen.', '', '');
