REPLACE INTO Language_DE_DE (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_GRANARY_HELP', '[ICON_FOOD] City Handel Lebensmittel [NEWLINE] [ICON_FOOD] Essen: +1 [ICON_RES_WHEAT] [ICON_RES_SPICES] [ICON_RES_SUGAR] [NEWLINE] [NEWLINE] Verbessert Korn Ressourcen.', '', '');
