REPLACE INTO Language_DE_DE (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_STABLE_STRATEGY', 'Die Stable erhöht die Geschwindigkeit der Produktion von Einheiten montiert und fügt die Produktion auf bestimmte Ressourcen.', '', '');
REPLACE INTO Language_DE_DE (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_STONE_WORKS_STRATEGY', 'Steinwerke können nur in einer Stadt, in der Nähe einer verbesserten [ICON_RES_STONE] Stein oder [ICON_RES_MARBLE] Marble Ressourcen aufgebaut werden. Steinwerke erhöhen [ICON_PRODUCTION] Produktion einer Stadt.', '', '');
