REPLACE INTO Language_DE_DE (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_WONDER_LEANING_TOWER_QUOTE', '[NEWLINE] [TAB] [TAB] "Die Renaissance des fünfzehnten Jahrhunderts war in vielen Dingen, großen und nicht durch das, was sie dann durch das, was sie erreicht konzipiert." [NEWLINE] [TAB] [TAB] - Walter Pater [NEWLINE] [TAB]', '', '');
REPLACE INTO Language_DE_DE (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_WONDER_LEANING_TOWER_DESC', 'Der Dom (Basilica di Santa Maria del Fiore) ist eine große Kathedrale im Herzen von Florenz, Italien. Es dauerte 140 Jahre, um zu bauen und beendete im Jahre 1436. Während der Bauarbeiten war Florenz eine Republik, Teil der vielen Stadtstaaten in Italien während der Zeit. Die Kathedrale spielte eine maßgebliche Rolle in der Republik und soll die Renaissance begonnen haben.', '', '');
