REPLACE INTO Language_DE_DE (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_WONDER_ITAIPU_DAM_HELP', 'Bietet Produktion in jeder Stadt. [NEWLINE] Stellt eine freie Ingenieur-Spezialist. [NEWLINE] Stellt eine freie Wasserkraftwerk in der Stadt. [NEWLINE] Muss auf einem Fluss gebaut werden.', '', '');
REPLACE INTO Language_DE_DE (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_WONDER_ITAIPU_DAM_QUOTE', '[NEWLINE] [TAB] [TAB] "Frauen denken, dass ein Ingenieur ist ein Mann im Hip Stiefel Bau eines Staudamms. Sie erkennen nicht, dass 95 Prozent der Technik liegt in einer schönen klimatisierten Büro gemacht." [NEWLINE] [ TAB] [TAB] -. Beatrice Alice Hicks, erste weibliche Ingenieur bei Western Electric [NEWLINE] [TAB]', '', '');
REPLACE INTO Language_DE_DE (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_WONDER_ITAIPU_DAM_DESC', 'Diese gigantische Staudamm am Rio Paraná erzeugt den meisten jährlichen Strom jeder Betriebswasserkraftwerk der Welt, liefert 90% des Stroms durch Paraguay verbraucht und 20% der von Brasilien verbraucht.', '', '');
