REPLACE INTO Language_ES_ES (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_COMMERCE_TEXT', 'La riqueza es la abundancia de recursos valiosos o posesiones materiales. El concepto de riqueza es significativa en todos los ámbitos de la economía, sin embargo, el significado de la riqueza depende del contexto y no hay acuerdo universal sobre la definición. A la riqueza de nivel más general es "cualquier cosa de valor", que captura tanto la naturaleza subjetiva de la palabra, y la idea de que no es un concepto fijo o estático.', '', '');
