REPLACE INTO Language_ES_ES (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_SOCIALPOLICY_COMMERCE_HEADING3_BODY', 'La rama de la riqueza se concentra en desarrollar el territorio de ancho, con una oferta nacional de [ICON_GOLD] Oro creado por las ciudades principales para comprar nuevas unidades y edificios en ciudades fronterizas subdesarrolladas.', '', '');
REPLACE INTO Language_ES_ES (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TECH_COMMERCE_HEADING4_BODY', 'La rama de la riqueza se concentra en desarrollar el territorio de ancho, con una oferta nacional de [ICON_GOLD] Oro creado por las ciudades principales para comprar nuevas unidades y edificios en ciudades fronterizas subdesarrolladas. Consulte las normas de Políticas Sociales para obtener más detalles.', '', '');
