REPLACE INTO Language_FR_FR (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_MACHU_PICHU_HELP', 'Une montagne à proximité des gains de la ville: [NEWLINE] [ICON_FOOD] Alimentation: 5 [NEWLINE] [ICON_GOLD] Or: 5 [NEWLINE] [ICON_CULTURE] Culture: 5 [NEWLINE] [ICON_PEACE] Foi: 5', '', '');
