REPLACE INTO Language_FR_FR (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_LONG_COUNT', 'Après avoir étudié la théologie, de recevoir un bonus de Grande-personne à la fin de chaque cycle de calendrier compte Long Maya (tous les 394 ans). [NEWLINE] Commence avec un Archer unique.', '', '');
