REPLACE INTO Language_FR_FR (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_RAZE_AND_HORSES', 'Camps barbares rendent en cas de défaite pour le double [ICON_GOLD] or. [NEWLINE] Commence par un cheval Archer.', '', '');
