REPLACE INTO Language_IT_IT (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_PRODUCTION_BUILDING_ALLOWS_WATER_ROUTES_EXTRA', 'Un percorso [ICON_CONNECTED] di strade, ferrovie, o acqua per la Capitale offre [ICON_GOLD] oro. [NEWLINE] [NEWLINE] Un percorso [ICON_CONNECTED_RAILROAD] delle ferrovie o acqua per la Capitale prevede +25% [ICON_PRODUCTION] Produzione (con [ COLOR_POSITIVE_TEXT] Ferrovie [ENDCOLOR] tecnologici). [NEWLINE] [NEWLINE] [COLOR_POSITIVE_TEXT] Fari [ENDCOLOR] forniscono un collegamento sicuro tra acqua e terra rotte commerciali.', '', '');
