REPLACE INTO Language_IT_IT (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_MACHU_PICHU_HELP', 'Una montagna vicino i guadagni della città: [NEWLINE] [ICON_FOOD] Cibo: 5 [NEWLINE] [ICON_GOLD] Oro: 5 [NEWLINE] [ICON_CULTURE] Cultura: 5 [NEWLINE] [ICON_PEACE] Faith: 5', '', '');
