REPLACE INTO Language_KO_KR (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_BAZAAR_HELP', '[ICON_TRADE] 근처의 고급 공급을 두 배로 [NEWLINE] [ICON_GOLD] 골드 : [ICON_RES_OIL] [NEWLINE] [ICON_GOLD] 골드에 +2 : 여기에서 무역 루트 당 +2 [NL 오아시스 [NEWLINE] [ICON_GOLD] 골드에 +2 】 【ICON_GOLD] 골드 : +1 럭셔리 자원에는 [NEWLINE] [NEWLINE] 고급 자원을 증가합니다.', '', '');
