REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_AMPHIB_WARLORD', 'Jednostki poruszają się szybciej wzdłuż rzek i rozpocząć promocję Amphibious.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_BONUS_AGAINST_TECH', ' +10% [ICON_GREAT_PEOPLE] Wielka Stopa z każdego ludzie Sojuszu i +20% [ICON_STRENGTH] Walka vs cywilizacji z kilku miast.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_BUFFALO_HORNS', 'Wojownicy i awangardy kosztują 50% mniej konserwacji. Wszystkie urządzenia wymagają 25% mniej doświadczenia, aby zarobić na kolejny awans.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_CAPITAL_BUILDINGS_CHEAPER', '25% [ICON_PRODUCTION] Produkcja w kierunku budynków, które już istnieją w [ICON_CAPITAL] Capital. Rozpoczyna się [ICON_RES_IRON] Żelazo w pierwszych 5 miastach.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_CITY_STATE_BONUSES', '25% wyższe zyski z zaprzyjaźnionych i sprzymierzonych Miasta-Państwa.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_CITY_STATE_FRIENDSHIP_SHORT', 'Hellenizacja', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_CITY_STATE_FRIENDSHIP', ' +2 [ICON_CULTURE] Kultura w każdym mieście. Miasto-państwo [ICON_INFLUENCE] Wpływ degraduje w połowie i odzyskuje na dwukrotność normalnej stawki.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_CONVERTS_SEA_BARBARIANS', 'Wewnętrzne szlaki handlowe zapewniają 10 [ICON_GOLD] złota do miejsca przeznaczenia.', '', '');
