REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_ENHANCED_GOLDEN_AGES', ' +50% [ICON_CULTURE] Kultura i +1 [ICON_MOVES] Ruch w czasie [ICON_GOLDEN_AGE] złotych wieków. [NEWLINE] rozpoczyna się od unikalnej Spearmana.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_FAITH_FROM_NATURE', ' +1 [ICON_PEACE] Wiara z nieutwardzonych lasów i dżungli, a 150% z naturalnych cudów rentowności. [NEWLINE] Rozpoczyna się Spearmana.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_FIGHT_WELL_DAMAGED', 'Uszkodzone jednostki walczyć w pełni sił. Łodzie rybackie i Isles dać [ICON_CULTURE] Kultura.', '', '');
