REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_GREAT_ANDEAN_ROAD', 'Jednostki poruszają się szybciej na Hills. Drogi i koleje koszt konserwacji na wzgórzach; pół kosztować gdzie indziej.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_GATEWAY_AFRICA', 'Cywile szybciej w pustyni. Wychodzące szlaki handlowe z różnymi cywilizacjami zarobić więcej [ICON_GOLD] Gold i [ICON_CULTURE] kultura, i przychodzących szlaki handlowe zarabiają więcej [ICON_GOLD] przez właściciela drogi.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_IGNORE_TERRAIN_IN_FOREST', 'Szybszy ruch w lasach i dżungli, które liczą się jako drogi w terenie dziećmi po badaniu koła. [NEWLINE] Uruchamia z unikalnym Swordsman.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_INGENIOUS', 'Wielcy naukowcy wydają 25% szybciej, a otrzymasz jeden w Piśmie. [NEWLINE] Rozpoczyna się Bowman.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_LAND_TRADE_GOLD_SHORT', 'Desert Przyczepa', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_LAND_TRADE_GOLD', 'Jednostki poruszają się szybciej na pustyni, karawany dotrzeć dalej, i religia szlaki handlowe rozprzestrzeniać szybciej od swoich miastach.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_LONG_COUNT', 'Po zbadaniu teologii, otrzymasz bonus wspaniały człowiek na końcu każdego cyklu kalendarza Majów: Long Count (co 394 lat). [NEWLINE] Uruchamia z unikalnym Archer.', '', '');
