REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_EXTRA_BELIEF', 'Wybierz jedną Wieży niż normalnie, gdy znaleźli religię.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_PHOENICIAN_HERITAGE', 'Porty uzyskać bezpłatną Harbor, a jednostki mogą przekroczyć góry. Rozpoczyna się z żeglarstwem, a Bireme i wojownika.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_PLANNED_ECONOMY_SHORT', 'Gospodarki planowej', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_PLANNED_ECONOMY', 'Jednostki patrz dalej, cywile szybciej i zyskujesz 1 mniej [ICON_HAPPINESS_4] Narodowy nieszczęście na miasto.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_POPULATION_GROWTH', 'Budynki żywności dodać 1 [ICON_CITIZEN] Ludność gdy zbudowane. [NEWLINE] Uruchamia z pracownikiem.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_RAZE_AND_HORSES', 'Obozy barbarzyńców poddać, gdy pokonał dwuosobowego [ICON_GOLD] Gold. [NEWLINE] Uruchamia z koniem Archer.', '', '');
