REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_AMERICAN_DREAM', 'Imigranci przenieść tutaj z cywilizacjami z niskim szczęścia i wysokiej amerykańskich wpływów kultury. Twoje obywatele migrują wzdłuż wewnętrznych [ICON_FOOD] szlaków handlowych Żywności.', '', '');
