REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_SEVEN_CITIES', 'Podwójne plony z naturalnych cudów, plus 100 [ICON_GOLD], gdy odkrył. [NEWLINE] Rozpoczyna się Scout, i otrzymuje Caravel w kompas.', '', '');
