REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_STRATEGIC_RICHES', 'Podwójna ilość od strategiczne zasoby i 3 dodatkowych głosów w Światowym Kongresie.', '', '');
