REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_VIKING_FURY', 'Szybsze i silniejsze wsiadania całej wody, a wręcz jednostki plądrować szybciej.', '', '');
