REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_TERROR', 'Miasta-państwa można tyranem poddać w obawie jak marionetki. Zamontowane i jednostek potyczki szybciej. Rozpoczyna się Scout.', '', '');
