REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_CONVERTS_LAND_BARBARIANS', 'Obozy barbarzyńców poddać, gdy pokonany, a jednostki lądowe kosztować mniej [ICON_GOLD] konserwacji. [NEWLINE] Uruchamia z koniem Archer.', '', '');
