REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_SCOURGE_OF_GOD', 'Obozy barbarzyńców poddać, gdy pokonany, a jednostki lądowe kosztować mniej [ICON_GOLD] konserwacji. [NEWLINE] Uruchamia z unikalnym Chariot.', '', '');
