REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TRAIT_AMSTERDAM_BOURSE', ' +5% [ICON_GOLD] Dochód Narodowy z każdego typu Luxury zasobu można sterować lub importu.', '', '');
