REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_TRADE_OFFICE_PEDIA', '(Todo)', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_TRIBUTE', 'Ottoman Tribute', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILD_MOAI_HELP', ' +3 [ICON_CULTURE] Kultura. [NEWLINE] +1 [ICON_CULTURE] Kultura na sąsiedniej Moai. [NEWLINE] [ICON_LOCKED] Musi być obok wybrzeża.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_CIV5_IMPROVEMENTS_MOAI_HELP', ' +3 [ICON_CULTURE] Kultura. [NEWLINE] +1 [ICON_CULTURE] Kultura na sąsiedniej Moai. [NEWLINE] [ICON_LOCKED] Musi być obok wybrzeża.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLYNESIAN_SCENARIO_BUILD_MOAI_HELP', ' +3 [ICON_CULTURE] Kultura. [NEWLINE] +1 [ICON_CULTURE] Kultura na sąsiedniej Moai. [NEWLINE] [ICON_LOCKED] Musi być obok wybrzeża.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_AMSTERDAM_BOURSE', 'Amsterdam giełda', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_AMSTERDAM_BOURSE_PEDIA', 'Amsterdam Stock Exchange jest uważany za najstarszy na świecie. Została założona w 1602 roku przez holenderskiego East India Company w zakresie obrotu swoich drukowanych akcje i obligacje. Został przemianowany następnie Amsterdam giełda i był pierwszym, który formalnie rozpocząć handel papierami wartościowymi.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_JELLING_STONES', 'Jelling Stones', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_JELLING_STONES_PEDIA', 'W Jelling rzeźbione masywne kamienie Kamienie runiczne z 10. wieku, znaleziono w miejscowości Jelling w Danii. Starszy z dwóch kamieni Jelling został podniesiony przez króla Gorm w pamięci jego żony Thyra. Większy z dwóch kamieni został podniesiony przez Harald Bluetooth, syna króla Gorm jest. Harald zbudowany drugi kamień w pamięci jego rodziców, obchody jego podbój Danii i Norwegii, a jego konwersji Duńczyków na chrześcijaństwo. Te inskrypcje runiczne na te kamienie są uważane za najbardziej znany w Danii.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_JADE_HALL', 'Jade Hall', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_JADE_HALL_PEDIA', 'Król Sejong nadzorował rozwój Chiphyonjon, Jade Sali Uczonych, profesjonalnych naukowców, którzy pomogli badawczych pytania społeczne i polityczne dla króla i rządu. Jade Hall, będą uważane za "think tank" przez rządy dzisiaj.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_INDUS_SANITATION', 'Indus sanitarne', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_INDUS_SANITATION_PEDIA', 'Harappan cywilizacja doliny rzeki Indus pierwsze znane systemy zaprojektowane miejskich sanitarnych na świecie około 2600 roku pne. Poszczególne domy lub grupy domów uzyskuje wodę ze studni. Ścieki kierowane z pokoi odstawić do kąpieli do objętych kanalizacji podszewka główne ulice. Masywne mury zostały spekulowali Harappan miastach ochrona przed zalaniem. Te systemy hydrotechniczne Harappan były jeszcze bardziej wydajne niż wielu obszarach Pakistanu i Indii dzisiaj, osiągnięcie dużych miast miejskich stosunkowo niewielkim rozwoju rolnictwa.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_UNIT_AMERICAN_PIONEER', 'Pionier', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_UNIT_AMERICAN_PIONEER_HELP', 'Zakłada nowe miasta, aby rozwinąć swoje imperium. [NEWLINE] [NEWLINE] [ICON_FOOD] rozwój miasta zostaje zatrzymany, gdy urządzenie jest w trakcie budowy. [NEWLINE]', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_UNIT_AMERICAN_PIONEER_STRATEGY', 'Pioneer amerykański Unique Unit. Porusza się szybciej niż osadnika, który zastępuje. [NEWLINE] [NEWLINE] Użyj tego urządzenia do budowy nowych miast. To nie może się bronić, więc towarzyszyć jej jednostki bojowej.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_UNIT_AMERICAN_PIONEER_PEDIA', 'Amerykańscy pionierzy mają żadnej z osób w historii Stanów Zjednoczonych, którzy wyemigrowali na zachód od wybrzeża Atlantyku do osiedlania się i rozwijać nowe obszary. Szczególnie odnosi się określenie tych, którzy zamierzali osiedlić terytorium nowego społeczeństwa europejskiego i amerykańskiego, chociaż może być terytorium zamieszkane przez lub wykorzystywane przez rdzennych mieszkańców. [NEWLINE] [NEWLINE] koncepcja pionierem i etos znacznie wyprzedzają migrację do części Stanów Zjednoczonych zwanego teraz państwa "zachodnie", jak wiele miejsc, teraz uważane za Wschodzie były również rozstrzygane przez pionierów z wybrzeża. Na przykład, Daniel Boone, kluczowa postać w amerykańskiej historii pioniera, osiedlił się w Kentucky, kiedy to "Mroczne i Krwawe ziemia" była jeszcze niezabudowana. [NEWLINE] [NEWLINE] Jednym ważnym wydarzeniem w zachodniej osady była ustawa Homestead, który dostarczył formalne przepisy, które reguluje proces rozliczeniowy.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_STEAM_MILL', 'Młyn parowy', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_STEAM_MILL_PEDIA', 'Przejście w kierunku fabryk maszynowych zaczęła oparte w gospodarce Wielkiej Brytanii w 1700 roku, wcześniej wymagającym pracy ręcznej i zwierzęta. Te prekursorami rewolucji przemysłowej rozpoczął mechanizacji przemysłu włókienniczego. Rozwój handlu był włączony przez wprowadzenie kanałów, ulepszonych dróg i linii kolejowych. Szersze wykorzystanie kół wodnych i wprowadzenia siły pary pchnął dramatyczny wzrost zdolności produkcyjnych. Rozwój wszystkich obrabiarek metali na początku 1800 roku ułatwiło budowę coraz bardziej wyrafinowanych maszyn produkcyjnych do produkcji w wielu różnych branżach.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_DOJO', 'Dojo', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_DOJO_PEDIA', 'Japońskie dojo ("miejsce drogi") jest miejscem spotkań do prowadzenia szkoleń, egzaminów i innych związanych z nimi spotkania dla "zrobić" sztuki. Dojo początkowo dodatkiem do świątyń. W Japonii każdy fizyczny obiekt szkoleniowy, w tym szkół zawodowych zapasach, może być nazywany ze względu na jego dojo sztuk walki bliskich korzeni.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_JAPANESE_SHRINE', 'Shinto Shrine', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_JAPANESE_SHRINE_PEDIA', 'W Japonii istnieją sanktuaria Shinto obok siebie z buddyjskich świątyń i są najliczniejsze, znanych i ważnych miejsc religijnych w Japonii. Przybytek Shinto jest budowa lub obiekt, którego głównym celem jest, aby pomieścić jedną lub więcej Shinto kami (duchy, siły przyrody, lub istotę). Kami mogą znajdować się w niemal wszystko naturalnym lub sztucznym, z Mt. Fuji do małego klejnotu. Jako takie, te kapliczki są powszechne i bardzo zróżnicowany zakres. Sama świątynia może być duży lub mały budynek, obiekt, wzdłuż drogowych, lub może nawet być wykonywane przez ludzi. [NEWLINE] [NEWLINE] Jak buddyjskich świątyń, sanktuariów Shinto nie są przede wszystkim miejscem kultu. Najważniejsze budynki sanktuarium są używane do przechowywania świętych przedmiotów. Istnieją wyspecjalizowane budynki dla niektórych obrzędów, ale te są zwykle dostępne tylko dla ograniczonej liczby uczestników. Religijne zgromadzenia masowe w stylu kościołów chrześcijańskich nie odbywają się regularnie i nie odbywają się wewnątrz świątyni. Jeśli wiele osób jest zaangażowanych w ceremonii, to zakładamy, świąteczny charakter i odbędzie się na zewnątrz.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_FOLKSKOLA', 'Folkskola', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_FOLKSKOLA_PEDIA', 'W 1842 r. parlament szwedzki wprowadził szkołę podstawową cztery lata dla dzieci w Szwecji, "Folkskola" (szkoła ludowa). Był to jeden z pierwszych systemów szkół publicznych na świecie. [NEWLINE] [NEWLINE] Szwedzkie dzieci są zobowiązani do uczęszczania grundskola, obowiązku szkolnego, w wieku od 7 do 16, gdzie grundskola jest połączenie szkoły podstawowej i średniej. Po grundskola, wielu Szwedów udział w planowej gymnasieskolan, liceum, wybierając opcję program uniwersytecki-przygotowawcze lub programu zawodowego. Studenci, którzy zdecydują program zawodowe normalnie zakończyć edukację po gymnasieskolan a studenci, którzy ukończą program uniwersytecki-przygotowawcze normalnie studiów wyższych na uniwersytecie lub college''u. Studenci, którzy ukończą program zawodowe mogą zapisać się na szkolnictwie wyższym, ale może trzeba podjąć dodatkowe kursy, zanim będą mogły być stosowane.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_BASILICA', 'Bazylika', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_BASILICA_PEDIA', 'Łacińska bazylika słowo (pochodzi od greckiego Stoa, komory trybunałem króla) było pierwotnie używane do opisania Roman budynek publiczny znajdujący się w forum rzymskiego miasta. Za tym idzie, to było stosowane do chrześcijańskich budynków tej samej formie. [NEWLINE] [NEWLINE] W 4 wieku, gdy władze cesarskie nie zalegalizował chrześcijaństwo z 313 edykt w Mediolanie, chrześcijanie byli gotowi do budowy większych i bardziej przystojny gmachy za kult niż ukradkowych miejsca spotkań zostali korzystających. Tradycyjne wzory architektoniczne świątyń były nieprzydatne z powodu związków z nie-chrześcijańskich wierzeń. Stare obrzędy świątynne wystąpił na zewnątrz pod gołym niebem w oczach bogów, ze świątyni w tle. Model Konstantyn chciał memorialise jego cesarskiej pobożności był wcześniej architektury (świecki) bazylik, trzymając uroczystości w pomieszczeniu.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_COTHON', 'Cothon', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_COTHON_PEDIA', 'Cothon (greckie: "naczynie do picia") jest sztuczny, chronione przystani wewnętrznej powszechne w Kartaginie w czasie wojen punickich c.200 pne [NEWLINE] Cothons były zazwyczaj w fenickiego świata.. Przykłady obejmują Motya (Sycylia) z 6 wieku pne, który dokonał niepewny cel, (o wymiarach 35 metrów x 51 metrów), choć sugeruje to cothon może nawet być zamykany i opróżniane, tworząc suchy dok. Mahdia (Tunezja) z 7 wieku pne, (która mierzona 72 metrów x 56 metrów), a jeden z Kition na Cyprze.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_VEDI', 'Vedi', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_VEDI_PEDIA', 'Vedi jest termin "zmiany" w religii wedyjskiej, najstarsza religia nadal praktykowane w czasach współczesnych. Takie ołtarze są podwyższone obudowa, ogólnie posypana Popular trawy i posiadające pojemniki do ognia ofiarnego.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_NASA_CENTER', 'NASA Centrum', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_NASA_CENTER_HELP', 'NASA Center to nowoczesne centrum badań, że Era daje wzrost technologii. Zapewnia również bezpłatny Spaceship zasobu fabryki.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_NASA_CENTER_PEDIA', 'Od momentu swojego powstania w 1958 roku, NASA dokonał wielu wspaniałych czynów naukowych i technologicznych w powietrzu i przestrzeni. Technologia NASA również został dostosowany do wielu zastosowań nonaerospace przez sektor prywatny. NASA pozostaje wiodącą siłą w badaniach naukowych i pobudzanie zainteresowania publicznego w eksploracji lotniczym, jak również nauki i technologii w ogóle. Co ważniejsze, nasza eksploracja przestrzeni nauczyło nas, by zobaczyć Ziemię, siebie i wszechświata w nowy sposób. Podczas gdy ogromne osiągnięcia techniczne i naukowe NASA wykazać wyraźnie, że ludzie mogą osiągnąć wcześniej niepojęte wyczyny, ale również są upokorzony przez świadomość, że Ziemia jest tylko małe "Blue Marble" w kosmosie.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_ABBEY', 'Opactwo', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_ABBEY_PEDIA', 'Abbey ma na swoim sercu praktyki kultu, na pomysł, aby nie dostać wszystkich do jednej usługi, ale w celu zapewnienia możliwości Opactwo wyznawcy kultu, które mogą wziąć udział w regularnie, jeśli nie co tydzień. Poprzez usługi, sztuki rozkwitła jako wyraz uwielbienia dla "Stwórcy". W opactw addycyjnych były samonośne, zaangażowany w polach uprawowych, hodowli zwierząt, działających na rynkach publicznych, i daje możliwości zatrudnienia dla społeczności. Opactw Celtic stały się ośrodkami kształcenia, ochrony świętych i literackich rękopisów i szkół nauczania.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_ABBEY_STRATEGY', 'Opactwo jest Celtic duchowe i kulturowe ośrodkiem każdej społeczności. Produkuje 1 [ICON_FOOD] Jedzenie [ICON_RESEARCH] Nauka [ICON_CULTURE] Kultura, [ICON_GOLD] Złoto i [ICON_PRODUCTION] produkcyjnych.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_ODEON', 'Odeon', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_ODEON_PEDIA', 'Odeon, od starożytnego greckiego ᾨδεῖον, Ōideion, dosłownie "śpiewa się" lub "budowanie na konkursach muzycznych". [NEWLINE] najwspanialszy Odeon był Odeon Heroda Attyka na klifie na południowy zachód od Akropolu w Atenach zbudowany w około 160 AD przez zamożnego sofista i retor Heroda Attyka w pamięci jego żony. Miał zakwaterowanie dla 8000 osób, i sufit wykonane z pięknie rzeźbionych belek z drewna cedrowego, prawdopodobnie z otwartej przestrzeni w centrum dopuszczenia światło. Został również obficie ozdobione zdjęć i innych dzieł sztuki.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_ODEON_STRATEGY', 'Odeon jest źródłem natychmiastowej [ICON_CULTURE] Kultury. Budowanie to pomaga szerzyć granice swojego imperium.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_BARBARY_PORT', 'Barbary port', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_BARBARY_PORT_PEDIA', 'Piraci Barbary, nazywane Barbary korsarze i korsarze osmańscy byli piraci i prywaciarzy, którzy sterowane z Afryki Północnej, w oparciu przede wszystkim w portach Tunis, Trypolisu i Algieru. [NEWLINE] [NEWLINE] Obszar ten był znany w Europie jako Barbary Coast , termin pochodzi od nazwiska jej mieszkańców berberyjskich. [NEWLINE] Oprócz zajmowania statków, są zaangażowane w Razzias, naloty na europejskich nadmorskich miast i wsi, głównie we Włoszech, Francji, Hiszpanii i Portugalii, ale także w brytyjskim Isles, Holandia i tak daleko, jak Islandia. Głównym celem ich ataków było uchwycić chrześcijańskich niewolników na rynek muzułmańskich w Afryce Północnej i na Bliskim Wschodzie.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_BASIN_IRRIGATION', 'Dorzecze nawadniania', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILDING_BASIN_IRRIGATION_PEDIA', 'Starożytni Egipcjanie praktykowali nawadniania Basin pomocą powodzi Nilu do zalewają działek, które zostały otoczone wałami. Wody powodzi odbył aż płodne osad osiadł przed nadwyżka wróciła do cieku wodnego. [NEWLINE] Istnieją dowody starożytnego faraona egipskiego Amenemheta III z XII dynastii (ok. 1800 pne), stosując naturalne jezioro w Fajum Oasis jako zbiornik do przechowywania nadwyżek wody do użytku podczas pory suchej, jezioro rosło rocznie od powodzi Nilu.', '', '');
