REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_ORAL_HISTORY_TEXT', 'Materiał jest ustna tradycja kulturowa i tradycja przekazywana ustnie z pokolenia na pokolenie. Wiadomości lub zeznania są ustnie przekazywane w mowie lub utworu i może mieć formę, na przykład, z Bajki, powiedzeń, ballady, pieśni i przyśpiewki. W ten sposób możliwe jest społeczeństwo do przekazywania historii mówionej, literatury, prawa werbalne werbalne i inne wiedzę następnym pokoleniom bez systemu pisania.', '', '');
