REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_COMMERCE_TEXT', 'Bogactwo jest mnóstwo cennych zasobów i dóbr materialnych. Pojęcie bogactwa jest istotna we wszystkich dziedzinach ekonomii, ale znaczenie bogactwa jest zależne od kontekstu i nie ma powszechnie uzgodnionej definicji. Na poziomie najbardziej ogólnym bogactwa jest "coś z wartości", który przechwytuje zarówno subiektywny charakter słowa, a pomysł, że to nie jest stałe lub statyczne pojęcie.', '', '');
