REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_SOCIALPOLICY_COMMERCE_HEADING3_BODY', 'Oddział Wealth koncentruje się na rozwoju terytorium szeroki, z krajowej podaży [ICON_GOLD] złota utworzonej przez głównych miastach na zakup nowych jednostek i budynków w miastach przygranicznych niezabudowane.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TECH_COMMERCE_HEADING4_BODY', 'Oddział Wealth koncentruje się na rozwoju terytorium szeroki, z krajowej podaży [ICON_GOLD] złota utworzonej przez głównych miastach na zakup nowych jednostek i budynków w miastach przygranicznych niezabudowane. Zobacz zasady dotyczące polityki społecznej na szczegóły.', '', '');
