REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_BRANCH_COMMERCE_HELP', 'Zdolności: [NEWLINE] [NEWLINE] [COLOR_POSITIVE_TEXT] Otwieracz [ENDCOLOR] [NEWLINE] [ICON_GOLD] Złoto: +10% {TEKST} [NEWLINE] [ICON_PRODUCTION] Produkcja: +10% {TEKST} [NEWLINE] [ICON_WORKER] Odblokowuje : Kanał Panamski [NEWLINE] [NEWLINE] [COLOR_POSITIVE_TEXT] finiszer [ENDCOLOR] [COLOR_GREY] (zakończyć wszystkie polityki w tym drzewa) [ENDCOLOR] [NEWLINE] [ICON_HAPPINESS_1] Szczęście: +1 na Rynku, Kuźnia [NEWLINE] [ICON_PEACE] mogą zakupić Wielkie Merchants z Wiary po wejściu do epoki przemysłowej.', '', '');
