REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_TRADE_PACT_TEXT', 'Wolny handel jest system polityki handlowej, która pozwala przedsiębiorcom na handel ponad granicami państwowymi, bez ingerencji z rządami obu krajów. Ze względu na zasady ekonomicznej przewagi komparatywnej, zapewnia partnerom handlowym z wzajemnych korzyści. W ramach tej polityki ceny są jedynym czynnikiem decydującym o przydziale zasobów i blisko odbicie popytu i podaży.', '', '');
REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_BRANCH_AESTHETICS_HELP', 'Zdolności: [NEWLINE] [NEWLINE] [COLOR_POSITIVE_TEXT] Otwieracz [ENDCOLOR] [NEWLINE] [ICON_PRODUCTION] +50% do produkcji budynków Kulturalnych [NEWLINE] [ICON_WORKER] Odblokowuje:. Uffizi [NEWLINE] [NEWLINE] [COLOR_POSITIVE_TEXT] finiszer [ENDCOLOR ] [COLOR_GREY] (zakończyć wszystkie polityki w tym drzewa) [ENDCOLOR] [NEWLINE] Wywołanie ukrytych miejsc starożytności. [NEWLINE] [ICON_TEAM_7] Umożliwia zakup wielkich ludzi kultury z [ICON_PEACE] Wiary po wejściu do epoki przemysłowej.', '', '');
