REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TECH_RATIONALISM_HEADING4_BODY', 'Oddział Znajomość polityki społecznej jest pełne polityki, które mogą zwiększyć swoje badania. Oddział ten będzie dostępny w epoce renesansu. Zobacz zasady dotyczące polityki społecznej na szczegóły.', '', '');
