REPLACE INTO Language_PL_PL (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_BRANCH_RATIONALISM_HELP', 'Zdolności: [NEWLINE] [NEWLINE] [COLOR_POSITIVE_TEXT] Otwieracz [ENDCOLOR] [NEWLINE] [ICON_TEAM_7] Wielki Naukowiec: +1 [NEWLINE] [ICON_WORKER] Odblokowuje: Bell Labs [NEWLINE] [NEWLINE] [COLOR_POSITIVE_TEXT] finiszer [ENDCOLOR] [ COLOR_GREY] (zakończyć wszystkie polityki w tym drzewa) [ENDCOLOR] [NEWLINE] +1 natychmiastowy [ICON_TEAM_5] Technologie. [NEWLINE] [ICON_PEACE] Czy zakup wielkich naukowców z [ICON_PEACE] Wiary po wpisaniu ery industrialnej.', '', '');
