REPLACE INTO Language_RU_RU (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_UNIT_HELP_SCOUT', 'Скауты преуспеть в разведку, и полезны в качестве дешевых гарнизонов города.', '', '');
