REPLACE INTO Language_RU_RU (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_WONDER_LEANING_TOWER_QUOTE', '[NEWLINE] [TAB] [TAB] "Ренессанс пятнадцатого века был, во многих вещах, большой, а тем, что она предназначена, то по тому, что она достигнута." [NEWLINE] [TAB] [TAB] - Уолтер Патер [NEWLINE] [TAB]', '', '');
REPLACE INTO Language_RU_RU (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_WONDER_LEANING_TOWER_DESC', 'Дуомо (Базилика Санта-Мария дель Фьоре) является большой собор расположен в самом центре Флоренции, Италия. Потребовалось 140 лет, чтобы построить, заканчивая в 1436. Во время строительства, Флоренция была республикой, в состав многих городов-государств в Италии во время. Собор играл основную часть в республике и, как говорят, началось Возрождение.', '', '');
