REPLACE INTO Language_RU_RU (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_BUILD_MOAI_HELP', ' +3 [ICON_CULTURE] Культура. [NEWLINE] +1 [ICON_CULTURE] Культура в соседней моаи. [NEWLINE] [ICON_LOCKED] Должен быть рядом с побережья.', '', '');
REPLACE INTO Language_RU_RU (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_CIV5_IMPROVEMENTS_MOAI_HELP', ' +3 [ICON_CULTURE] Культура. [NEWLINE] +1 [ICON_CULTURE] Культура в соседней моаи. [NEWLINE] [ICON_LOCKED] Должен быть рядом с побережья.', '', '');
