REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_DIPLO_DISCUSS_MESSAGE_DEC_FRIENDSHIP_TT', 'Doubles income from [ICON_RESEARCH] Research Agreements and [ICON_GOLD] Mutual Open Borders with this leader.[NEWLINE][NEWLINE]An alliance promotes friendly diplomacy and deters outside aggressors. Allies may request diplomatic or economic assistance, and failure to support your ally may upset them.', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_DIPLO_OPEN_BORDERS_TT', 'With [COLOR_YIELD_GOLD]mutual open borders[/COLOR], 1% of the combined gross [ICON_GOLD] Income of both players adds to your gold rate (2% if Allied).[NEWLINE][NEWLINE]Allows the other player''s units to pass through your territory.[NEWLINE][NEWLINE](Lasts {1_Num} turns.)', '', '');
