REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_MISSION_CULTURE_BOMB_DISABLED_COOLDOWN', 'You have created a Great Work recently, and may not do so again for another {1_Num} turns.', '', '');
