REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_MENTORS_TEXT', 'Mentoring is an organized process for the transmission of knowledge, social capital, and the psychosocial support perceived by the recipient as relevant to work, career, or professional development. Mentoring entails informal communication, usually face-to-face and during a sustained period of time, between a person who is perceived to have greater relevant knowledge, wisdom, or experience (the mentor) and a person who is perceived to have less (the protégé).', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_BRANCH_HONOR_HELP', 'Abilities:[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Opener[ENDCOLOR][NEWLINE][ICON_VIEW_CITY] Reveals barbarian camps.[NEWLINE][ICON_STRENGTH] +10% Strength against barbarians.[NEWLINE][ICON_CULTURE] Killing barbarians gives 2 Culture per strength of defeated enemies.[NEWLINE][ICON_WORKER] Unlocks: Temple of Artemis[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Finisher[ENDCOLOR] [COLOR_GREY](finish all policies in this tree)[ENDCOLOR][NEWLINE] +15% Strength for Melee Units. [NEWLINE] +25% Great General Rate', '', '');
