REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_BRANCH_LIBERTY_HELP', 'Abilities:[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Opener[ENDCOLOR][NEWLINE][ICON_CULTURE] Culture: +1 in all cities[NEWLINE][ICON_PRODUCTION] Production: +1 in all cities[NEWLINE][ICON_WORKER] Unlocks: Terracotta Army[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Finisher[ENDCOLOR] [COLOR_GREY](finish all policies in this tree)[ENDCOLOR][NEWLINE][ICON_TEAM_7] Great Person: +1 of choice', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_COLLECTIVE_RULE_HELP', '[ICON_TEAM_2] Settler: +1[NEWLINE][ICON_PRODUCTION] Production: +25% for Settlers', '', '');
