REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_CITIZENSHIP_HELP', '[ICON_TEAM_2] Workers: +1[NEWLINE][ICON_WORKER] Worker Improvement Rate: +25%', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_REPRESENTATION_HELP', '[ICON_HAPPINESS_1] +1 National Happiness per [ICON_CONNECTED] connected city.[NEWLINE][ICON_HAPPINESS_4] -5% Unhappiness from [ICON_CITIZEN] Population in cities that are not [ICON_OCCUPIED] Occupied.', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_REPUBLIC_HELP', '[ICON_TEAM_9] Free City Defense building in all cities (current, new, and conquered). With the appropriate tech researched.[NEWLINE][ICON_PRODUCTION] +1 Production per City Defense Building.', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_BRANCH_TRADITION_HELP', 'Abilities:[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Opener[ENDCOLOR][NEWLINE][ICON_CULTURE] Culture: +3 in Capital[NEWLINE][ICON_WORKER] Unlocks: Banaue Rice Terraces[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Finisher[ENDCOLOR] [COLOR_GREY](finish all policies in this tree)[ENDCOLOR][NEWLINE][ICON_GREAT_PEOPLE] Great Person Rate: +50%[NEWLINE][ICON_CULTURE] Doubles cultural border expansion to tiles far from cities.[NEWLINE][ICON_PEACE] Can purchase Great Engineers with Faith starting in the Industrial era.', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_ORAL_HISTORY', 'Storytellers', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_ORAL_HISTORY_HELP', '[ICON_CULTURE] Culture: +2 per Village and Palace', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_ORAL_HISTORY_TEXT', 'Oral tradition is cultural material and tradition transmitted orally from one generation to another. The messages or testimony are verbally transmitted in speech or song and may take the form, for example, of folktales, sayings, ballads, songs, or chants. In this way, it is possible for a society to transmit verbal history, verbal literature, verbal law and other knowledge across generations without a writing system.', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_CEREMONIAL_RITES', 'Ceremonial Rites', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_CEREMONIAL_RITES_HELP', '[ICON_CULTURE] Culture: +1 per Culture Building[NEWLINE][ICON_CULTURE] 1 free Cultural Building in your oldest 4 cities.', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_CEREMONIAL_RITES_TEXT', 'A ceremony is an event of ritual significance performed on a special occasion. Ceremonies may have a physical display or theatrical component: dance, a procession, the laying on of hands. A declaratory verbal pronouncement may be associated with the occasion.[NEWLINE][NEWLINE]A ceremony may mark a rite of passage in a human life, marking the significance of birth, adulthood, initiation into a group, or death. Sometimes a ceremony may only be performed by a person with certain authority. Society-wide ceremonies may mark annual or seasonal or recurrent events, such as the vernal equinox, winter solstice and other annual astronomical positions.', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_LANDED_ELITE_HELP', '[ICON_FOOD] Surplus Food: +25% per city[NEWLINE][ICON_FOOD] Food: +2 per city[NEWLINE][ICON_FOOD] Food: +1 per 4 population in the capital', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_OLIGARCHY_HELP', '[ICON_RANGE_STRENGTH] City Attack: +50% from garrisons[NEWLINE][ICON_GOLD] No maintenance for garrisoned units.', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_ARISTOCRACY_HELP', '[ICON_PRODUCTION] Production: +15% for Wonders[NEWLINE][ICON_HAPPINESS_1] Happiness: +1 per 10 [ICON_CITIZEN] Citizens', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_MONARCHY_HELP', '[ICON_CITIZEN] Citizens in the Capital create +1 [ICON_GOLD] Gold and +0.5 [ICON_HAPPINESS_1] Happiness.', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_MENTORS', 'Mentors', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_MENTORS_HELP', '+5 [ICON_RESEARCH] Science per turn.[NEWLINE]+5% [ICON_RESEARCH] Science from Science Buildings.', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_MENTORS_TEXT', 'Mentoring is an organized process for the transmission of knowledge, social capital, and the psychosocial support perceived by the recipient as relevant to work, career, or professional development. Mentoring entails informal communication, usually face-to-face and during a sustained period of time, between a person who is perceived to have greater relevant knowledge, wisdom, or experience (the mentor) and a person who is perceived to have less (the protégé).', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_BRANCH_HONOR_HELP', 'Abilities:[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Opener[ENDCOLOR][NEWLINE][ICON_VIEW_CITY] Reveals barbarian camps.[NEWLINE][ICON_STRENGTH] +10% Strength against barbarians.[NEWLINE][ICON_CULTURE] Killing barbarians gives 2 Culture per strength of defeated enemies.[NEWLINE][ICON_WORKER] Unlocks: Temple of Artemis[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Finisher[ENDCOLOR] [COLOR_GREY](finish all policies in this tree)[ENDCOLOR][NEWLINE][ICON_HAPPINESS_1] City Happiness: +1 per Barracks.[NEWLINE][ICON_MOVES] Purchased units can move immediately.', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_MILITARY_TRADITION_HELP', ' +2 [ICON_TEAM_2] Defense Units at the Capital.[NEWLINE]+20% [ICON_PRODUCTION] Production for military units and buildings.', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_SPOILS_OF_WAR', 'Spoils of War', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_SPOILS_OF_WAR_HELP', '[ICON_GOLD] Salvage gold from the equipment of defeated enemies (3 times the unit strength).[NEWLINE][ICON_CITY_STATE] +25% rewards from capturing City-States.', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_SPOILS_OF_WAR_TEXT', 'Looting by a victorious army during war has been common practice throughout recorded history. For foot soldiers, it was viewed as a way to supplement their often meagre income and was part of the celebration of victory. On higher levels, the proud exhibition of loot was an integral part of the typical Roman triumph, and Genghis Khan was not unusual in proclaiming that the greatest happiness was "to vanquish your enemies... to rob them of their wealth..."[NEWLINE][NEWLINE]In warfare in ancient times, the spoils of war included the defeated populations, which were often enslaved, and the women and children, who were often absorbed into the victorious country''s population. In other pre-modern societies, objects made of precious metals were the preferred target of war looting, largely because of their easy portability. In many cases looting was an opportunity to obtain treasures that otherwise would not have been obtainable. Since the 18th century, works of art have increasingly become a popular target. In the 1930s and even more so during World War II, Nazi Germany engaged in large scale and organized looting of art and property.', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_GLADIATORS', 'Gladiators', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_GLADIATORS_HELP', '[ICON_HAPPINESS_1] City Happiness: +1 per Colosseum.[NEWLINE][ICON_TEAM_2] +2 Defense Units at the Capital.[NEWLINE][ICON_GOLD] 2 military units cost no maintenance.', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_GLADIATORS_TEXT', '(todo)', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_WAR_EPICS', 'War Epics', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_WAR_EPICS_HELP', ' +1 [ICON_HAPPINESS_1] City Happiness from Culture buildings.', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_WAR_EPICS_TEXT', 'An epic (from the Ancient Greek adjective ἐπικός (epikos), from ἔπος (epos) "word, story, poem") is a lengthy narrative poem, ordinarily concerning a serious subject containing details of heroic deeds and events significant to a culture or nation.[NEWLINE][NEWLINE]War epics are epic poems or a literary works of epic scope which seek or are believed to capture and express the essence or spirit of a particular nation, or an ethnic or linguistic group with aspirations to independence or autonomy. National epics frequently recount the origin of a nation, a part of its history, or a crucial event such as decisive victories or the vanquishing of bitter foes, in the development of national identity', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_MILITARY_CASTE_HELP', '[ICON_HAPPINESS_1] City Happiness: +1 per Garrison[NEWLINE][ICON_CULTURE] Culture: +3 per Garrison', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_DISCIPLINE_HELP', '[ICON_TEAM_7] Great General: +1[NEWLINE][ICON_MOVES] Movement: +1 for Great Generals.[NEWLINE][ICON_STRENGTH] +15% Strength for all Units adjacent to other friendly military units.', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_PROMOTION_DISCIPLINE_HELP', '[ICON_STRENGTH] +15% when [COLOR_POSITIVE_TEXT]adjacent[ENDCOLOR] to friendly military units.', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_PROFESSIONAL_ARMY_HELP', '[ICON_GOLD] Upgrade cost: -50% for units[NEWLINE][ICON_PRODUCTION] Production: +15% for All units', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_WARRIOR_CODE_HELP', '[COLOR_POSITIVE_TEXT]Warrior Code[ENDCOLOR][NEWLINE]', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_BRANCH_PIETY_HELP', 'Abilities:[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Opener[ENDCOLOR][NEWLINE][ICON_HAPPINESS_1] Happiness: +4[NEWLINE][ICON_PEACE] Faith +2[NEWLINE][ICON_WORKER] Unlocks: Stonehenge[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Finisher[ENDCOLOR] [COLOR_GREY](finish all policies in this tree)[ENDCOLOR][NEWLINE]Choose one new Pious belief.', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_CHARITY', 'Charity', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_CHARITY_HELP', '[ICON_GOLD] Gold: +25% from Temples[NEWLINE][ICON_GOLD] Gold: +2 from Temples[NEWLINE][ICON_GOLD] Gold: +3 Holy Sites.', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_CHARITY_PEDIA', 'The word "charity" comes from the Old French word "charité" which, in turn, was derived from the Latin "caritas".[NEWLINE][NEWLINE]The practice of charity means the voluntary giving of help to those in need who are not related to the giver, though the original meaning is more involved with the concepts of preciousness, dearness, high price. Charitable giving is done directly or by means of a charitable trust or other worthy cause. These institutions evolved to carry out the labor of assisting the poor, and came to be called, called ''charities''. In modern times ''charities'' provide the bulk of charitable giving, in terms of monetary value. The types of charities include orphanages, food banks, religious institutes dedicated to care of the poor, hospitals, organizations that visit the homebound and imprisoned, and many others.', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_INSPIRATION', 'Inspiration', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_INSPIRATION_HELP', '[ICON_TEAM_7] Free Great Prophet: +1', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_INSPIRATION_PEDIA', 'The word "inspiration" comes from the Latin noun inspiratio and from the verb inspirare. Inspirare is a compound term resulting from the Latin prefix in (inside, into) and the verb spirare (to breathe).[NEWLINE][NEWLINE]In classic Roman times, inspirare had already come to mean "to breathe deeply" and assumed also the figurative sense of "to instill [something] in the heart or in the mind of someone".[NEWLINE][NEWLINE]Hence ''inspiration'' refers to an unconscious burst of creativity in a literary, musical, or other artistic endeavour. The concept has origins in both Hellenism and Hebraism. The Greeks believed that inspiration came from the muses, as well as the gods Apollo and Dionysus. Similarly, in the Ancient Norse religions, inspiration derives from the gods, such as Odin. Inspiration is also a divine matter in Hebrew poetics. In the Book of Amosthe prophet speaks of being overwhelmed by God''s voice and compelled to speak. In Christianity, inspiration is a gift of the Holy Spirit.', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_TOLERANCE', 'Tolerance', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_TOLERANCE_HELP', '[ICON_HAPPINESS_1] City Happiness: +1 per Religious Building[NEWLINE][ICON_PEACE] Cities with multiple religions also get the Pantheon bonus of the city''s second most popular religion.', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_TOLERANCE_PEDIA', 'Tolerance or toleration is "the practice of deliberately allowing or permitting a thing of which one disapproves. One can meaningfully speak of tolerating, ie of allowing or permitting, only if one is in a position to disallow”. It has also been defined as "to bear or endure" or "to nourish, sustain or preserve". Toleration may signify “no more than forbearance and the permission given by the adherents of a dominant religion for other religions to exist, even though the latter are looked on with disapproval as inferior, mistaken or harmful”[NEWLINE][NEWLINE]Tolerance is an attitude of mind that implies non-judgmental acceptance of different lifestyles or beliefs, whereas toleration implies putting up with something that one disapproves of.[NEWLINE][NEWLINE]Historic examples of ''tolerance'' abound. For instance in an act of tolerance, the Persian king Cyrus the Great was believed to have released the Jews from captivity in 539–530 BC, and permitted their return to their homeland.', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_UNITY', 'Unity', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_UNITY_HELP', '[ICON_GOLDEN_AGE] Golden Age Turns: 10[NEWLINE][ICON_GOLDEN_AGE] Golden Ages require 50% less [ICON_HAPPINESS_1] National Happiness', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_UNITY_PEDIA', 'Unity refers to the ties in a society that bind people to one another. The term is generally employed in sociology and the other social sciences.[NEWLINE][NEWLINE]In simple societies it may be mainly based around kinship and shared values. In more complex societies there are various theories as to what contributes to a sense of social solidarity.[NEWLINE][NEWLINE]This brings in the associated term of ''solidarity'' of which it is said:[NEWLINE]"Solidarity does not assume that our struggles are the same struggles, or that our pain is the same pain, or that our hope is for the same future. Solidarity involves commitment, and work, as well as the recognition that even if we do not have the same feelings, or the same lives, or the same bodies, we do live on common ground." - Sara Ahmed', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_MEDITATION', 'Meditation', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_MEDITATION_HELP', '[ICON_PEACE] Faith: +1 Shrines, Temples[NEWLINE][ICON_PEACE] Faith Costs: -33%', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_MEDITATION_PEDIA', '(todo)', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_DEVOTION', 'Devotion', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_DEVOTION_HELP', 'x', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_DEVOTION_PEDIA', 'Devotion may be defined as: ardent, often selfless affection and dedication, as to a person or principle. Worship, from the Old English worthscipe, meaning worthiness or worth-ship is an act of religious devotion usually directed towards a deity. Closely allied to the concept of love, devotion is often hoped to be reciprocated with some form of boon or blessing.[NEWLINE][NEWLINE]Devotees, as those who practice devotion are called wanting to receive these blessings, are often single-minded to the point of fanaticism. Thus whilst the concept of ''devotion'' is benign, many historical examples of religious devotion are often extreme and bloody, as devotees vie for the affection of the devoted with more outrageous displays.', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_BRANCH_COMMERCE', 'Wealth', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_BRANCH_COMMERCE_CAP', 'WEALTH', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_CIV5_POLICY_COMMERCE_HEADING', '{TXT_KEY_POLICY_BRANCH_COMMERCE}', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_SOCIALPOLICY_COMMERCE_HEADING3_TITLE', '{TXT_KEY_POLICY_BRANCH_COMMERCE}', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_COMMERCE_TEXT', 'Wealth is the abundance of valuable resources or material possessions. The concept of wealth is significant in all areas of economics, yet the meaning of wealth is context-dependent and there is no universally agreed upon definition. At the most general level wealth is &quot;anything of value&quot;, which captures both the subjective nature of the word, and the idea that it is not a fixed or static concept.', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_SOCIALPOLICY_COMMERCE_ADV_QUEST', 'What is the Wealth social policy branch about?', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_SOCIALPOLICY_COMMERCE_HEADING3_BODY', 'The branch of Wealth focuses on developing wide territory, with a national supply of [ICON_GOLD] Gold created by core cities to purchase new units and buildings in undeveloped frontier cities.', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TECH_COMMERCE_HEADING4_BODY', 'The branch of Wealth focuses on developing wide territory, with a national supply of [ICON_GOLD] Gold created by core cities to purchase new units and buildings in undeveloped frontier cities. See the rules on Social Policies for details.', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_TECH_COMMERCE_HEADING4_TITLE', 'The Wealth Branch', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_BRANCH_COMMERCE_HELP', 'Abilities:[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Opener[ENDCOLOR][NEWLINE][ICON_GOLD] Gold: +10% {TXT_KEY_IN_ALL_CITIES}[NEWLINE][ICON_PRODUCTION] Production: +10% {TXT_KEY_IN_ALL_CITIES}[NEWLINE][ICON_WORKER] Unlocks: Panama Canal[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Finisher[ENDCOLOR] [COLOR_GREY](finish all policies in this tree)[ENDCOLOR][NEWLINE][ICON_HAPPINESS_1] Happiness: +1 per Market, Smithy[NEWLINE][ICON_PEACE] Can purchase Great Merchants with Faith upon entering the Industrial era.', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_MERCANTILISM_HELP_OLD', ' +2[ICON_MOVES] Movement for non-combat units.[NEWLINE]+25% [ICON_GOLD] Income from [ICON_CONNECTED] Domestic Trade.[NEWLINE]-25% [ICON_GOLD] Cost for roads and railroads.', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_PROTECTIONISM_HELP', '[ICON_GOLD] Gold: +1 per Village[NEWLINE][ICON_PRODUCTION] Production: +1 per Mine, Lumbermill', '', '');
REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_TRADE_UNIONS_HELP', '[ICON_GOLD] Can purchase Landsknechts.[NEWLINE][ICON_TEAM_2] 2 Landsknechts appear at the Capital.', '', '');
